/**
 * QR Encoder
 *
 * @namespace LUNGO.Sugar
 * @class QR
 * @version 1.0
 *
 * @author Javier Jimenez Villar <javi@tapquo.com> || @soyjavi
 */
LUNGO.Sugar.QR=function(e){_generateUrl=function(a,c,d){var b="http://chart.googleapis.com/chart?cht=qr";b+=_data(a);b+=_size(d);b+=_level(c);return b};_data=function(a){return"&chl="+a};_size=function(a){return"&chs="+a+"x"+a};_level=function(){return"&chld=L|0"};return{encode:function(a,c,d,b){if(a=e.dom(a)){c=_generateUrl(c,d,b);a.append('<img src="'+c+'">')}}}}(LUNGO);
